import React, { Component } from 'react';
import ImageLinkForm from '../components/ImageLinkForm/ImageLinkForm';
import Logo from '../components/Logo/Logo';
import Navigation from '../components/Navigation/Navigation';
import FaceRecognition from '../components/FaceRecognition/FaceRecognition';
import Rank from '../components/Rank/Rank';
import Particles from 'react-particles-js';
import './App.css';

import Clarifai from 'clarifai';
import Signin from '../components/Signin/Signin';
// const stub = ClarifaiStub.grpc();
const app = new Clarifai.App({
  apiKey: '95ba94e97294463c9e0f590c8670d199'
 });
// This will be used by every Clarifai endpoint call.
// const metadata = new grpc.Metadata();
// metadata.set("authorization", "Key {95ba94e97294463c9e0f590c8670d199}");
const ParticleOptions={
  particles: {
    number:{
      value:30,
      density:{
        enable:true,
        value_area:800
      }
    }
}
}

class App extends Component{
  constructor(props){
    super(props);
    this.state={
      input:'',
      imageUrl:'',
      box:{},
      route:'signin'
    }
    this.onUpdate = this.onUpdate.bind(this);
    
    this.onImageSubmit = this.onImageSubmit.bind(this);
  }
  onUpdate = (event)=>{
    console.log(event.target.value)
    this.setState({input:event.target.value})
  }
  calculateFacelocation=(data)=>{
    const Face=data;
    const image=document.getElementById('imagebox');
    const width=Number(image.width);
    const height=Number(image.height);
    return {
      leftcol:Face.left_col*width,
      toprow:Face.top_row*height,
      rightcol:width-(Face.right_col*width),
      bottomrow:height-(Face.bottom_row*height)

    }
  }
  displayBox=(box)=>{
    console.log(box)
    this.setState({box:box})
  }
  onImageSubmit = ()=>{
    console.log('click')
    this.setState({imageUrl: this.state.input});
    app.models
    .predict('f76196b43bbd45c99b4f3cd8e8b40a8a',this.state.input)
    .then(response =>
      {
        console.log('success',response.outputs[0].data.regions[0].region_info.bounding_box)
        this.displayBox(this.calculateFacelocation(response.outputs[0].data.regions[0].region_info.bounding_box))
      }) 
      .catch(err => console.log(err));
  }
  onRouteChange=()=>{
    this.setState({route:'home'})
  }
  render(){
    return(
      <div className="App">
        <Particles className="particles" params={ParticleOptions} />
        
         <Navigation />
         {this.state.route==='signin'?
         <Signin onRouteChange={this. onRouteChange}/>
        :
        <div>
        <Logo />
         <Rank />
         <ImageLinkForm onUpdate={this.onUpdate} onImageSubmit={this.onImageSubmit}/>
         <FaceRecognition box={this.state.box}imageUrl={this.state.imageUrl}/>
         </div>
        }
         
         
      </div>
     
  
    );
  }
 
  
}

export default App;
